export default function App() {
  return (
    <>
      <div className="pane top-pane">
        <Editor language="xml" displayName="HTML" value="html" />
      </div>
      <div className="pane">
        <iframe
          srcDoc={srcDoc}
          title="output"
          sandbox="allow-scripts"
          frameBorder="0"
          width="100%"
          height="100%"
        />
      </div>
    </>
  );
}
